    static buildSprites(){
        const step = 8
        AstroidConstants.sheet.width = 2200
        AstroidConstants.sheet.height = 1250
        const ctx = AstroidConstants.sheet.getContext('2d')!
        ctx.setTransform(1,0,0,1,0,0)
        let canvasWidth = 0
        let canvasHeight = 0
        let final = [0,0]
        let max = 0
        // create row of sprites on virtual sprite sheet
        for(let i = 0;i<15;i++){
            ctx.save()
            const index = i
            const clipPaths:any[] = []
            const rowInfo:any[] = []
            const pathInfo:any[] = []
            const ch = canvasHeight
            const img:HTMLImageElement = document.createElement('img')
            const im = require(`../../assets/asteroids/asteroid_${(i+1)<10 ? `0${i+1}`:i+1}_with_cracks.png`)
            const inf = AstroidConstants.getIndex(index+1)!
            const spriteSize = inf.size
            const spriteRows = inf.rows
            const width = spriteSize.x
            const height = spriteSize.y
            inf.paths.forEach(item=>{clipPaths.push(new Path2D(item))})
            for(let j = 0; j<inf.hits; j++){
                const cols = inf.hits / spriteRows
                const rowNum = Math.floor(j/cols)
                const colNum = j % cols
                rowInfo.push([canvasWidth,ch,width,height,colNum*width,rowNum*height,inf.initScale])
                canvasWidth += width
            }
            max = height
            if(clipPaths.length>0){
                for(let x = 0;x<6;x++){
                    const sepx = inf.stepX+(inf.stepX*x)
                    const sepy = inf.stepY+(inf.stepY*x)
                    //             |  vCan.x  ,   vCan.y   |   vSize    |                    vPos                       |initial scale| offset |
                    pathInfo.push([canvasWidth,canvasHeight,width,height,(inf.hits/inf.rows-1)*width,(inf.rows-1)*height,inf.initScale,sepx,sepy])
                    canvasWidth += width + sepx
                    max = Math.max(max,height+sepy)
                }
            }
            canvasHeight += max
            img.src = im
            canvasWidth = 0
            max = 0
            ctx.restore()
            //get row data
            const rowData = [...rowInfo,...pathInfo]
            //make row and push into table.locs[]
            const row = {size:[(pathInfo[pathInfo.length-1][0] + pathInfo[pathInfo.length-1][2] + pathInfo[pathInfo.length-1][7])*pathInfo[pathInfo.length-1][6],(pathInfo[pathInfo.length-1][3] + pathInfo[pathInfo.length-1][8])*pathInfo[pathInfo.length-1][6]], 
                        hits:[...rowInfo.map(e=>{return [e[0]*e[6], e[1]*e[6], e[2]*e[6], e[3]*e[6], -(e[2]*e[6])/2, -(e[3]*e[6])/2, e[2]*e[6], e[3]*e[6]]})], 
                        explode:[...pathInfo.map(e=>{return [e[0]*e[6], e[1]*e[6], e[2]*e[6], e[3]*e[6], -(e[2]*e[6])/2, -(e[3]*e[6])/2, e[2]*e[6], e[3]*e[6]]})]}//add offsets
            AstroidConstants.table.locs.push(row)
            //console.log(`size: ${row.size}`)
            //console.log(row.hits[1])            
            //console.log(rowData.map(i=>{return `[${i[0]*i[6]}, ${i[1]*i[6]}, ${i[2]*i[6]}, ${i[3]*i[6]}, ${-(i[2]*i[6])/2}, ${-(i[3]*i[6])/2}, ${i[2]*i[6]}, ${i[3]*i[6]}]`}).join(", "))
            img.onload = (ev)=>{
                if(true){
                    // load and scale existing sprites
                    for(let j = 0; j<rowInfo.length; j++){
                        const specs = AstroidConstants.getIndex(j+1)
                        if(false){
                            //do something else
                        }else{
                            ctx.save()
                            ctx.scale(rowInfo[j][6],rowInfo[j][6])
                            ctx.translate(rowInfo[j][0], rowInfo[j][1])
                            ctx.drawImage(img, rowInfo[j][4], rowInfo[j][5], rowInfo[j][2], rowInfo[j][3], 0, 0, rowInfo[j][2], rowInfo[j][3])
                            ctx.restore()
                        }
                    }
                    // create load and scale dynamic sprites
                    if(true){
                        for(let x = 0;x<pathInfo.length;x++){
                            const sep = step+(step*x)
                            let tx = 0
                            let ty = 0
                            for(let n=0; n<clipPaths.length;n++){
                                if(clipPaths.length === 4){
                                    //only offset in +x or +y axis to line up with row top
                                    tx = (n===1 || n===2) ? 0:pathInfo[x][7]
                                    ty = (n===2 || n===3) ? 0:pathInfo[x][8]
                                }else if(clipPaths.length === 3 && i===3){
                                    tx = (n===1 || n===2) ? 0:pathInfo[x][7]
                                    ty = (n===0 || n===2) ? 0:pathInfo[x][8]
                                }else if(clipPaths.length === 3){
                                    tx = (n===1) ? 0:pathInfo[x][7]
                                    ty = (n===2) ? 0:pathInfo[x][8]
                                }else if(clipPaths.length === 2 && (i===5 || i===6 || i===7)){
                                    tx = (n===1) ? 0:pathInfo[x][7]
                                    ty = (true) ? 0:pathInfo[x][8]
                                }else if(clipPaths.length === 2 && (i===8 || i===13 || i===14)){
                                    tx = (n===1) ? 0:pathInfo[x][7]
                                    ty = (n===1) ? 0:pathInfo[x][8]
                                }else if(clipPaths.length === 2){
                                    tx = (n===0) ? 0:pathInfo[x][7]
                                    ty = (n===1) ? 0:pathInfo[x][8]
                                }
                                ctx.save()
                                ctx.scale(pathInfo[x][6],pathInfo[x][6])
                                ctx.translate(pathInfo[x][0],pathInfo[x][1])
                                ctx.translate(pathInfo[x][2]/2+tx,pathInfo[x][3]/2+ty)
                                ctx.clip(clipPaths[n])
                                ctx.drawImage(img, pathInfo[x][4], pathInfo[x][5], pathInfo[x][2], pathInfo[x][3], -pathInfo[x][2]/2, -pathInfo[x][3]/2, pathInfo[x][2], pathInfo[x][3])
                                ctx.restore()
                                tx = 0
                                ty = 0
                            }
                        }
                    }
                }
            }
        }
    }
